import React from "react";
class FilterAPI extends React.Component {

    constructor(props) {
        super(props);
        this.state({
            employees: [],
            Fl: '',
        })
    }

   
    handlechange = (event) => {
        this.setState({ Fl:event.target.value })
    }
    componentDidMount() {
       fetch('https://www.breakingbadapi.com/api/')
            .then((response) => response.json())
            .then(employeesli => {
                this.setState({ employees: employeesli });
            });
    }

    render() {
        return (
            <div>
                <input type="text" placeholder="Search......" onChange={this.clickchange}/>
                <table class="table">
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Name</th>
                        </tr>
                    </thead>
                    <tbody>{
                        this.state.employees.filter((name) => name.name.toLowerCase().includes(this.state.Fl)).map((employee) => (
                            <tr>
                                <th scope="row">{employee.id}</th>
                                <td>{employee.name}</td>
                            </tr>
                        ))
                    }


                    </tbody>
                </table>

            </div>
        )
    }


}
export default FilterAPI;



