import React from "react";
class FilterAPI extends React.Component {

    constructor(props) {
        super(props);
        this.state({
            employees: [],
            Fl: ''
        })
    }

    clickchange = (event) => {
        this.setState(
            {
                Fl: (event.target.value)
            }
        )
    }
    componentDidMount() {
        const response = fetch('https://www.breakingbadapi.com/api/')
            .then((response) => response.json())
            .then(employeesli => {
                this.setState({ employees: employeesli });
            });
    }

    render() {
        return (
            <div>
                <input type="text" placeholder="Search......" onChange={this.clickchange} />
                <table class="table">
                    <thead>
                        <tr>
                            <th>id</th>
                            <th>Name</th>
                        </tr>
                    </thead>
                    <tbody>{
                        this.state.employees.filter((name) => name.name.toLowerCase().includes(this.state.Fl)).map((user) => (
                            <tr>
                                <th scope="row">{user.char_id}</th>
                                <td>{user.name}</td>
                            </tr>
                        ))
                    }


                    </tbody>
                </table>

            </div>
        )
    }


}
export default FilterAPI;
