import React from "react";

class FilterAPI extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            employee: [],
            search: '',
        }

    }

    componentDidMount() {
        fetch('https://api.themoviedb.org/3/movie/{movie_id}?api_key=<<api_key>>&language=en-US')
            .then((response) => response.json())
            .then(employeeList => {
                this.setState({ employee: employeeList });
            });
    }
    handlechange = (e) => {
        this.setState({ search: e.target.value })
    }

    render() {
        return (

            <>
                <br /><br /><br />
                <input type="text" id="search" placeholder='Search...' onChange={this.handlechange}></input>
                <br /><br />
                <table class="table">
                    <thead class="table-primary">
                        <tr>
                            <th scope="col">ID</th>
                            <th scope="col">Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.employee.filter((name) => name.name.toLowerCase().includes(this.state.search)).map((user) => (
                                <tr>
                                    <th scope="row">{user.char_id}</th>
                                    <td>{user.name}</td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>
            </>
        )
    }
}

export default FilterAPI;
