import React,{useEffect , useState}  from "react";
import Singlemovie from './Singlemovie';
import {Route} from 'react-router-dom'; 
function MovieList(){
const[Movies , setMovies] = useState([]);
useEffect( ()=> {
 const response =  fetch('https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=bc63f0c08d018cf26d4cf02b8efde4d7') 
.then((response)=> response.json())
.then(data => {setMovies(data);
    console.log(data);
    });
    });
return (
<div class="row"  >
    {Movies.length > 0 && 
    Movies.map(m =>(
 <div class="column col-sm-3">
 <div class="card-body">
    <img src={m.backdrop_path}/>
 <h3 class="card-title">{m.title}</h3>
      <p class="card-text">  {m.overview}</p>
      <h6 className="card-title"><b style={{ color: "red" }}>Rate :</b> {m.vote_average}
</h6>
      <a href="" class="btn btn-primary">Watch</a>
</div>
</div>)
    )
    }
    </div>
);

}
export default MovieList ;
